let loadImg = (src) => {
           return new Promise((resolve, reject) => {
               let img = document.createElement('img')
               img.src = src
               document.body.append(img)
               setTimeout(() => {
                   resolve(true)
               }, 1000)
           })
       }
       const imgs = ['banner1.jpg', 'banner2.jpg', 'banner3.jpg', 'banner4.jpg'];
       // 依次加载图片
       async function fAync(imgs) {
           for (let i of imgs) {
               await loadImg(i)
           }
       }
       fAync(imgs);

