class Watch{
   constructor(){
        this.events={}
   }
    on(eventName,ck){
        if(typeof this.events[eventName]==='object'){
            this.events[eventName].push(ck) 
        }else{
            this.events[eventName]=[ck]
        } 
    }
    emit(eventName,...arg){
        this.events[eventName].forEach(item=>{
            item(...arg)
        })
    }
}
const watch=new Watch();
watch.on('num',(n1,n2,n3)=>{
    console.log(n1,n2,n3)
})
watch.emit('num',1,2,3)
watch.on('aa',(obj)=>{
    console.log(obj)
})
watch.emit('aa',{
    name:'zs',
    age:18
})

