             let score = await fetchData('/api/evaluation/'+item2.id+'?filter='+item[0].id);
                // 获取当前科目的分数
             return score.score;
        });
        let eachTotscore = totscore.reduce((tot,each)=>tot+each); // reduce函数获取总分

        return {
            id:item[0].id,
            name:item[0].name,
            average:Math.floor(eachTotscore/eachLength)
        }
    })
