let number=0;
        function gua(num,res){//创建函数
            if(num===0){//如果num===0
                res=[]//那么res为空数组
            }
            var i=1;//声明一个i等于1
            while(i<3){//while循环
                if(num+i<=10){//如果num+i<=10,不够十个台阶
                    let _res=[...res];//展开res
                    _res.push(i);//向_res里push  i
                    if(num+i===10){//如果num+i===10,跳的够10个台阶
                    number++;//number++
                    console.log(number,'res',_res)
                }else{
                    gua(num+i,_res)//回调
                }
                }
                i++
            }

        }
        gua(0)

let fs=require('fs');
let path=require('path');
let mpath=path.join(__dirname,'wenjian')//文件路径
function siz(wen){
   	 let size=fs.statSync(wen);//文件信息(大小)
    let res=size.size/1024/1024;//size换算成mb
    if(res<2){//如果大于2
    	return '小于限定值'//返回'小于限定值'
    }else if(res>2){//如果大于2
    	return '大于限定值'//返回大于限定值
    } else {//否则
    	return '等于限定值'//返回等于限定值
    }
	}
siz(mpath)

