let loadImg = (src) => {
  return new Promise((resolve, reject) => {
    let img = document.createElement('img')
    img.src = src
    document.body.append(img)
    setTimeout(() => {
      resolve(true)
    }, 1000)
  })
}
const imgs = ['./img/1.png', './img/2.png', './img/3.png'];
async function fSync(imgs) {
  for (let i of imgs) {
    await loadImg(i)
  }
}
fSync(imgs);
