module.exports.isLarger = (fileSize, unit = 2) => {
    return (fileSize / 1024 / 1024) <= unit ? false : true
}

const fs = require('fs');
const path = require('path');
const utils = require('./lib/utils');
const sourcePath = path.join(__dirname, 'source');
const targetPath = path.join(__dirname, 'target');

const Copy = (sourcePath, targetPath) => {
    //判断目标路径是否存在
    let exist = utils.exists(targetPath)
    if (!exist) {
        fs.mkdirSync(targetPath)
    }
    //遍历文件夹
    fs.readdir(sourcePath, (error, files) => {
        if (error) return console.error(error);
        //循环得到的数组
        files.forEach(file => {
            //源路径
            let sourceFilepath = path.join(sourcePath, file)
            //目标路径
            let targetfilepath = path.join(targetPath, file)
            fs.stat(sourceFilepath, (error, stats) => {
                // stas.isDirectory() 当前文件是不是文件夹
                // stas.isFile() 当前文件是不是文件
                if (stats.isFile()) {
                    if (utils.isLarger(stats.size)) {
                        let readStream = fs.createReadStream(sourceFilepath);
                        // let writeStream = fs.createWriteStream(targetfilepath);
                        // 通过writeFile 写入文件
                        readStream.on('data',(context)=>{
                            utils.writeFile(targetfilepath, context, (error, result) => {
                                if (error) return console.log(error);
                                console.log(result)
                            })
                        })
                        // readStream.pipe(writeStream)
                    } else {
                        //读文件
                        utils.readFile(sourceFilepath, (error, context) => {
                            if (error) return console.error(error);
                            //拼接写入文件的路径

                            //写入
                            utils.writeFile(targetfilepath, context, (error, result) => {
                                if (error) return console.log(error);
                                console.log(result)
                            })
                        })
                    }

                } else {
                    //递归调用
                    Copy(sourceFilepath, targetfilepath)
                }
            })

        })
    })
}

