let loadImg = (src)=> {
  return new Promise(function (resolve, reject) {
     resolve=fSync(imgs);
  });
};
const imgs = ['url1', 'url2', 'url3'];
async function fSync(imgs){
      await formap(imgs);
   }
function formap(imgs){
        var index=0;
     for (var i=0;i<imgs.length;i++){
        imgs[i].src=imgs[i];
        index++;
        if(index>=imgs.length){
            index=0;
        }
    }
 }
