// 封装加载图片的promise  
let loadImg = (src)=>{
    return new Promise((resolve, reject)=>{
        // write your code here
      let img=new Image()；//创建img
      img.onload={//加载成功
        resolve(img);
        setTimeout(()=>{
          document.body.append(img);//添加到页面
        },1000)
      },
     img.onerror={//加载失败
      reject("加载失败")
    }
    img.src=src;
    })
}   
const imgs = ['url1', 'url2', 'url3'];
// 依次加载图片
async function fAsync(imgs){
   // write your code here
  return [
    await loadImg(imgs[0].src),
    await loadImg(imgs[1].src)，
    await loadImg(imgs[2].src)
  ]
}

fAsync(imgs);

