function steamroller(arr) {
  // I'm a steamroller, baby
  var newArr = [];
  var temp;
  function ifArray(arr) {
    for(var i = 0; i < arr.length; i++) {
      //若为数组则继续迭代
      if(Array.isArray(arr[i])) {
        temp = arr[i];
        ifArray(temp);
      }
      else {
        newArr.push(arr[i]);
      }
    }
  }
  ifArray(arr);
  return newArr;
}
