const STUDENTS = '/api/students',
    COURSES = '/api/courses',
    EVALUATION = '/api/evaluation/'


function fetchData(api) {
    return new Promise(resolve => {
        let data = null;
        switch (api) {
            case "/api/students":
                data = [{
                    id: 1,
                    name: 'John',
                    classroomId: [1]
                }, {
                    id: 2,
                    name: 'Tom',
                    classroomId: [1, 2]
                }];
                break;
            case '/api/courses':
                data = [{
                    id: 1,
                    name: 'history',
                    studentId: [1]
                }, {
                    id: 2,
                    name: 'algebra',
                    studentId: [1, 2]
                }];
                break;
            case '/api/evaluation/':
                data = [{
                    id: 1,
                    score: 75,
                    totalScore: 75
                }, {
                    id: 2,
                    score: 70,
                    totalScore: 140
                }];
                break;
        }
        resolve(data);
    });
}


async function creatobj(classId) {
    let studentArr = null;
    await fetchData(COURSES).then(data => {
        data.forEach(element => {
            if (element.id === classId) {
                studentArr = element.studentId;
            }
        });
    })
    let arr = [];
    for (let v of studentArr) {
        let obj = {};
        await fetchData(STUDENTS).then(data => {
            data.forEach(item => {
                if (item.id === v) {
                    obj.id = v;
                    obj.name = item.name;
                }
            })
        })
        await fetchData(EVALUATION).then(data => {
            data.forEach(item => {
                if (item.id === v) {
                    obj.id = v;
                    obj.average = item.totalScore;
                }
            })
        })
        arr.push(obj);
    }
    return arr
}
