student_answer:
// 封装加载图片的promise  
    let loadImg = (src) => {
        return new Promise((resolve, reject) => {
            let img = new Image();
            img.src = src;
            img.onload = () => {
                resolve(img)
            }
        })
    }
    const imgs = ['url1', 'url2', 'url3'];
    // 依次加载图片
    async function fSync(imgs) {
        for (let v of imgs) {
            await loadImg(v).then(img=>{
          		document.body.appendChild(img);
            })
        }
    }
    fSync(imgs);

