const computedPrice = (arr) =>{
    let allPrice = 0;
    let group = [];
    arr.forEach((item,i)=>{
        let price = item.type.reduce((prev,every)=>{
            return prev+ every.price
         },0);
        allPrice +=price;
        group.push({
            typeName:item.groupName,
            price
        })
    });
    return {
        allPrice,
        group
    }
}
