let oUl = document.getElementsByTagName('ul')[0]; // dom节点ul标签第一个
     let li = document.getElementsByTagName('li'); //dom节点li标签
     const {
         map,
         forEach
     } = []; //
     li = map.call(li, item => { // 将所有li标签转成数组
         let key = 2 * item.getAttribute('key') - 1; // 获取所有key并-1
         item.setAttribute('key', key); // 设置key值
         return item; //返回新的数组
     });
     oUl.innerHTML = ''; //ul标签内容为空
     forEach.call(li, item => { //循环li添加到ul中
         oUl.appendChild(item);
     })

