let lis = document.querySelectorAll('li')
let ul = document.querySelector('ul')
1.给每个li绑定点击事件
lis.forEach((item, index) => {
	item.onclick = () => {
		console.log(index)
	}
})
2.事件委托
ul.onclick = function(e) {
	let target = e.target
	if (target.tagName === 'LI') {
		lis.forEach((item, index) => {
			if (item.innerHTML === target.innerHTML) {
				console.log(index)
			}
		})
	}
}
