student_answer:
 let loadImg = (src) => {
   return new Promise((resolve, reject) => {
     let img = document.createElement('img');
     img.src = src;
     document.body.append(img);
     resolve(true);
   })
 }
const imgs = ['banner1.jpg', 'banner2.jpg', 'banner3.jpg'];
async function fAync(imgs) {
  for (let i of imgs) {
    await loadImg(i);
  }
}
fAync(imgs);

