function getRate(num,sum){
  	let str='';
	if(num==''){
    	return 1;
    }else if(num<0){
      return 2;      
    }else if(sum&&num>sum){
      return 3;      
    }else if(sum&&sum<0){
      return 4;      
    }else if(sum&&sum%1!=0){
      return 5;      
    }else{
    	for(let i=0;i<Math.round(num);i++){
      		str+='★' ;	 
       };
      if(sum){
        for(let j=0;j<sum-num;j++){
              str+='☆';
          }
      	}else{
         for(let j=0;j<5-num;j++){
              str+='☆';
          }
        };
      return str;
    }
}
