let loadImg = (src) => {
        return new Promise((resolve, reject) => {
            let img = new Image();
            img.src=src;
             document.body.appendChild(img);
             setTimeout(()=>{
                 resolve(img)

             },2000)
            // write your code here
        })
    }
    const imgs = ['url1', 'url2', 'url3'];
    // 依次加载图片
    async function fAsync(imgs) {
        // write your code here
        for(let v of imgs){
            await loadImg(v);
        }
    }
    fAsync(imgs);

