let cunter = null;
(function counter(add) {
  let allPrice = 0;
  let group = [];
  add.forEach(ele => {
    let price = ele.type.reduce(function(prev, cur) {
      allPrice += cur.price;
      return cur.price + prev;
    }, 0)
    group.push({
      typeName: ele.groupName,
      price
    })
  });
  cunter = { allPrice, group };
  return cunter;
