let loadImg = (src) => {
       return new Promise((resolve, reject) => {
           // write your code here
           let img = new Image();
           img.src = src;
           img.onload = function() {
               document.body.appendChild(img);
               resolve("图片加载成功")
           };
           img.onError = function() {
               reject("加载失败");
           };
       })
   }
   const imgs = ['url1', 'url2', 'url3'];
   // 依次加载图片
   async function fSync(imgs) {
       // write your code here
       for (let i = 0; i < imgs.length; i++) {
           await loadImg(imgs[i]);
       }
   }
