let all = path.join(__dirname, "./aaa.mp3"); // 返回当前的绝对路径 C:\Users\将来Zzz\Desktop\流式方法写入\fczll.mp4
function isStream(all) {
    let big = fs.statSync(all); // 获取文件信息
    let lett = big.size / 1024 / 1024;
    if (lett > 2) {
        let dram = fs.createReadStream(all); // 创建一个流式读取
        let writeStream = fs.createWriteStream("./a.mp3"); // 以流式写入
        dram.pipe(writeStream);
    } else {
        fs.readFileSync(all);
    }
}
