function jump(number) {
  if (number <= 0) {
    return 0;
  }
  if (number == 1) {
    return 1;
  }
  if (number == 2) {
    return 2;
  }
  var jumpOne = 1;
  var jumpTwo = 2;
  var jumpN = 0;
  for (var i = 3; i <= number; i++) {
    jumpN = jumpOne + jumpTwo;
    jumpOne = jumpTwo;
    jumpTwo = jumpN;
  }
  return jumpN;
}
