public class Solution {
    public int JumpFloor(int n) {
        int former1 = 1;
        int former2 = 2;
        int target = 0;
        if(n == 0 ) {
            return 0;
        }
    if(n == 1 ) {
            return 1;
        }
        if(n == 2 ) {
            return 2;
        }else {
            for(int i = 3;i <= n;i++) {
                target = former1 + former2;
                former1 = former2;
                former2 = target;
            }
            return target;
        }
    }
}

const fs = require('fs');
class readFile {
    constructor(url) {
        this.url = url;
        this.init()
    }
    init() {
        let size = fs.statSync(this.url).size; //b 1kb=1024b
        console.log(size)
        if (size > 2000) {
            // 默认为大文件
            this.readStream()
        } else {
            // 默认为小文件
            this.readSmallFile()
        }
    }
    readStream() {
        let rs = fs.createReadStream(this.url); // 可读流
        let ws = fs.createWriteStream('./big/' + this.url); //可写流
        rs.pipe(ws) //
    }
    readSmallFile() {
        fs.readFile(this.url, 'utf-8', (err, con) => {
            if (err) throw err;
            fs.writeFileSync('./small/' + this.url, con)
        })
    }
}
