const fs = require('fs'); //引入fs
class readFile {
    constructor(url) {
        this.url = url;
        this.init()
    }
    init() {
        let size = fs.statSync(this.url).size;
        console.log(size)
        if (size > 2000) { //判断文件大小
            this.readStream() //大于 2000
        } else {
            this.readSmallFile() //小予
        }
    }
    readStream() {
        let rs = fs.createReadStream(this.url);
        let ws = fs.createWriteStream('./big/' + this.url);
        rs.pipe(ws)
    }
    readSmallFile() {//小予2000是放入small
        fs.readFile(this.url, 'utf-8', (err, con) => {
            if (err) throw err;
            fs.writeFileSync('./small/' + this.url, con)
        })
    }
}
new readFile('fs.mp4')//读取文件大小
