//这段代码是将ul中的所有li元素的自定义属性key的值替换为1,3,5,7,9 单数的形式，通过两次遍历，map 相当于创建一个新的li节点，通过改变this指向来调用，forEach 是遍历添加到ul中，达到替换key值得目的
 //获取到ul 和 li的dom节点
        let oUl = document.getElementsByTagName('ul')[0];
        let li = document.getElementsByTagName('li');
        //解构出map，forEach
        const {
            map,
            forEach
        } = [];
        // 遍历li，call改变this指向，指向li
        li = map.call(li, item => {
            //定义一个变量接收新数据
            let key = 2 * item.getAttribute('key') - 1;
            //将新的数据添加到自定义属性中
            item.setAttribute('key', key);
            //返回每一项、
            return item;
        });
        //清空 ul 的内容
        oUl.innerHTML = '';
        //遍历li
        forEach.call(li, item => {
            //将li追加节点到ul中
            oUl.appendChild(item);
        })

