long long forg_fun3_result(vector<long long>& r,int n)
{
    if(r[n]!=-1)
        return r[n];

    if(n >= 3)
        r[n] = forg_fun3_result(r,n-1)+forg_fun3_result(r,n-2);


    return r[n];
}
long long forg_fun3(int n)
{
    if(n <= 0)
        return 0;
    if(n == 1)
        return 1;
    if(n ==2)
       return 2;
    vector<long long> r(n+1,-1);
    r[0] = 0; 
    r[1] = 1;
    r[2] = 2;
    return forg_fun3_result(r,n);

}

