function random (n,m){
  return Math.floor(Math.random()*(m-n+1)+n)
}

function jumpMethod(){
  let num = random(1,2);
  let arr = []
  for(var i =0; i<10 ; i++){
    arr.push(num)
    num+=num
    if(num==10){
      return arr
    }
  }
}

function readFileSize (path){
  const fs = require("fs");
  if(fs.existsSync(path)){
    fs.stat(path,(err,stats)=>{
      if(err)console.log(err)
      if(stats.size<(2048*1024){
        return fs.readFileSync(path)
      }
    })
  }
}

function arr(foo){
