const fs = require("fs");

function readFile(src) {
    let num = fs.statSync(src).size;
    console.log(num);
    if (num > 2048) {
        let rs = fs.createReadStream(src);
        let ws = fs.createWriteStream("./big/" + src);
        rs.pipe(ws)
    } else {
        fs.readFile(src, (err, con) => {
            if (err) throw err;
            fs.writeFileSync("./small/" + src, con)
        })
    }
}


