public int JumpFloor(int n)
     {
         if (n < 0)
             return 0;
         int[] fibArry = { 0, 1, 2 };
         if (n < 3)
             return fibArry[n];
         return JumpFloor(n - 1) + JumpFloor(n - 2);
     }


