var obj={};
let write=document.getElementById("txt");
let show=document.getElementById("show-txt");
Object.defineProperty(obj,"txt",{
    get:function(){
        return obj;
    },
    set:function(value){
        show.innerHTML=value;
    }
});
write.addEventListener("keyup",function(e){
    obj.txt=e.target.value;
})

