	    let cats = [ 
    { name: 'Tom', weight: 300 },
    { name: 'Lucy', weight: 400 }, 
    { name: '‘Lily', weight: 700 }, 
    { name: '‘Jerry’', weight: 600 }]
   function maopao(cats){ 
        for(let i=1; i<cats.length;i++){
            // console.log(cats[i]);
            let obj={};
            for(let j=0;j<cats.length-i;j++){
                if(cats[j].weight<cats[j+1].weight){
                    obj=cats[j+1];
                    cats[j+1]=cats[j];
                    cats[j]=obj;
                }
                // console.log(1)
            }
        }
        return cats;
   }

    console.log(maopao(cats));
