function jumpFloor(number)
{
    if(number===0){
        return 0;
    }
    else if(number===1){
        return 1;
    }
    else if(number===2){
        return 2;
    }
    else{
        var pre = 1;//只能跳一个
        var cur = 2;//或者跳两个
        for(var i = 2;i<number;i++){
            cur+=pre;
            pre = cur-pre;
        }
        return cur;
    }
}

