var topics = {}

        class Watch {
            on(topic, listener) {
                // 实现该方法逻辑
                if (!topics[topic]) {
                    topics[topic] = []
                }
                topics[topic].push(listener)
            }
            emit(topic, data) {
                // 实现该方法逻辑 
                if (!topics[topic] || topics[topic].length < 1) {
                    return
                }
                topics[topic].forEach(function(listener) {
                    listener(data || {})
                });
            }
        }

        //调用示例如下：
        const w1 = new Watch();
        w1.on('www', (options) => {
            console.log(options)
        });
        w1.emit('www', 'success');

