function computedPrice(car) {
  let fruitsPrice = 0,
      vegetablesPrice = 0;

  car.forEach(item => {

    if (item.groupName === '水果') {
      // console.log(item.type);

      fruitsPrice = item.type.reduce((prev, cur) => {
        return cur.price + prev;
      }, 0);

    } else {

      vegetablesPrice = item.type.reduce((prev, cur) => {
        return cur.price + prev;
      }, 0);

    }
  })

  let newObj =
      {
        allPrice: +fruitsPrice + vegetablesPrice * 1,
        group: [
          {
            typeName: '水果',
            price: +fruitsPrice
          },
          {
            typeName: '蔬菜',
            price: +vegetablesPrice
          }
        ]
      }

  return newObj;
}

