function sum(n) {
            var count = 0,
                num1 = 1,
                num2 = 2;

            if (n <= 0) return 0;
            if (n == 1) return 1;
            if (n == 2) return 2;
            for (var i = 3; i <= n; ++i) {
                count = num1 + num2;
                num1 = num2;
                num2 = count;
            }
            return count;
        }

