let fs = require("fs");


function copySmall(src, newsrc) {
    fs.writeFileSync(newsrc, fs.readFileSync(src))
};

function copyBig(src, newsrc) {
    let rs = fs.createReadStream(src);
    let ws = fs.createWriteStream(newsrc);
    rs.pipe(ws);
};

function copyFile(src, newsrc, size = 24000) {
    fs.stat(src, (err, stat) => {
        stat.size > size ? copyBig(src, newsrc) : copySmall(src, newsrc)
    })
}

 var num=5,
     $rating=$("#rating"),
     $item=$rating.find(".rating-item");
//点亮
var lightOn=function(num){
	$item.each(function(index){
		if(index<num){
			$(this).css('background-position','0px -40px');
		}else{
			$(this).css('background-position','0px 0px');
		}
	});
}
//初始化
lightOn(num);
//事件绑定
$item.on('mouseover',function(){
	lightOn($(this).index()+1);
}).on('click',function(){
	num=$(this).index()+1;
});
$rating.on('mouseout',function(){
	lightOn(num);
});
