let oUl = document.getElementsByTagName('ul')[0];
let li = document.getElementsByTagName('li');
const {map,forEach} = [];   //解构数组给map,forEach  此时map,forEach表示 [].map  [].forEach
li = map.call(li,item=>{	//this 指向给li  进行遍历
  let key = 2 * item.getAttribute('key') - 1;	//对每个li的key值进行 *2-1 得出值为1 3 5 7
  item.setAttribute('key',key);   //给 每个li进行重新赋值
  return item;
});
oUl.innerHTML = '';	 //清空ul
forEach.call(li,item=>{  //遍历li
  oUl.appendChild(item);   //给ul追加每个li
})

