const fs = require("fs");
const path = require("path");

let copybig = (a,b)=>{
    let ws = fs.createReadStream(a);
    let rs = fs.createWriteStream(b)
    ws.pipe(rs)
}

let copmin = (a,b)=>{
   let rads = fs.readFileSync(a)
   fs.writeFileSync(b,rads)
}

let cops = (st,endir,size = 2000)=>{
    if(!fs.existsSync(endir)){
        fs.mkdirSync(endir)
    }
    let arr = fs.readdirSync(st);
    arr.forEach(el=>{
        if(fs.statSync(path.join(st,el)).isFile()){
            let sizes = fs.statSync(path.join(st,el)).size;
            sizes > size?copybig(path.join(st,el),path,join(endir,el)) : copmin(path.join(st,el),path.join(endir,el))
        }else{
            cops(path.join(st,el),path.join(endir,el))
        }
    })
}

