student_answer:
let loadImg = (src) => {
    return new Promise((resolve, reject) => {
        // write your code here
       let img=document.createEvent('img');
       img.src=src;
       document.body.append(img);
       setTimeout(()=>{
           resolve(true)
       },1000) 
    })
}
const imgs = ['url1', 'url2', 'url3'];
// 依次加载图片
async function fSync(imgs) {
    // write your code here
    for(let i of imgs){
        await loadImg(i)
    }
}
fSync(imgs);

