function convert(list, parentKey, currentKey, rootValue) {
  let tree = {};
  let children = [];
  let child = [];
  let id = '';
    list.forEach(val=>{
        if(val.parentId === rootValue){
            children.push(val)
            id = val.parentId
        }else{
            if(val.parentId === val.parentId){
                child.push(val)
                id = val.parentId
            }
        }
        tree = {
            "id":id,
            "children":children
        }
    })

  return tree
}

