const APIS = {
  STUDENTS: '/api/students',
  COURSES: '/api/courses',
  EVALUATION: '/api/evaluation/'
}; 
function fetchData(api) {
  return new Promise(resolve => {
    let data = null;
    switch (api) {
      case APIS.STUDENTS:
        data = [{
          id: 1,
          name: 'John',
          classroomId: 75
        }, {
          id: 1,
          name: 'Tom',
          classroomId: 75
        }];
        break;
      case APIS.COURSES:
        data = [{
          id: 'history',
          studentId: 1
        }, {
          id: 'algebra',
          studentId: 1
        }];
        break;
      case APIS.EVALUATION:
        data = {
          id: '200',
          score: 50,
          totalScore: 100
        };
        break;
    } 
    resolve(data);
  });
} 
```
