struct TreeNode {
	int val;
    TreeNode *left;
    TreeNode *right;
    TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 };
class Solution {
public:
    vector<vector<int>> pathSum(TreeNode* root, int sum) {
        vector<vector<int>> res;//存储最后结果
        vector<int> path;//用来存储遍历的路径
       // int value;//用来存储路径累加和
        target_path(root,sum,res,path);//寻找目标路径
        return res;
    }
private:
    void target_path(TreeNode* node,int sum,vector<vector<int>> &res,vector<int> &path){
        if(!node){
            return;
        }
        path.push_back(node->val);//将遍历到的节点的值push到路径
        sum-=node->val;//将路径的值累加
        if(!node->left && !node->right && sum==0){//判断目前存储的路径是否符合要求
            res.push_back(path);
        }
            target_path(node->left, sum, res,path);//递归遍历左节点
            target_path(node->right, sum, res,path);//再递归遍历右节点
             path.pop_back();//遍历到叶节点后，再退回遍历其他节点
             sum+=node->val;
    }
};

const findMostProductivePigChildrenCount = (dom) => {
    let result = [];
    // 广度优先遍历
    const find = (targets, result) => {
      // 下一轮待遍历的节点
      let childrenTargets = [];
      // 当前轮遍历后子节点的最大值
      let max = 0;
      // 当前轮遍历
      for (let target of targets) {
        // 如果有子节点
        if (target.childElementCount) {
          // 将子节点放入待遍历的数组中
          childrenTargets.push(...target.children);
          // 当前轮遍历后子节点的最大值
          max = Math.max(max, target.childElementCount)
        }
      }
      result.push(max);
      // 进行下一轮遍历
      if (childrenTargets.length) {
        find(childrenTargets, result)
      }
    };
    // 初始化遍历，根节点放入队列中
    find([dom], result);
    return result;
  }
