public int JumpFloor(int target) {       
      if(target == 1){
           return 1;
       }
       if(target == 2){
           return 2;
       }
       //第一次有两种选择，然后根据不同的选择，然后开始不同的下一步，但是下一步还是一样有两种选择
       return JumpFloor(target - 1) + JumpFloor(target - 2);
}

beforeUpload(file) { 				
				var testmsg=file.name.substring(file.name.lastIndexOf('.')+1)				
