				const extension2 = testmsg === 'xlsx'
				const isLt2M = file.size / 1024 / 1024 < 10     //这里做文件大小限制
				if(!extension && !extension2) {
					this.$message({
						message: '上传文件只能是 xls、xlsx格式!',
						type: 'warning'
					});
				}
