// 封装加载图片的promise  
let loadImg = (src) => {
    return new Promise((resolve, reject) => {
        let img = document.createElement('img');
        img.src = src;
        img.onload = function() {
            document.body.appendChild(img);
            resolve();
        }
    })
}
const imgs = ['url1', 'url2', 'url3'];
// 依次加载图片
async function fSync(imgs) {
    imgs.forEach((item) => {
        await loadImg(item)
    })
}
fSync(imgs);
