const fs = require('fs');
function readFile(fil){
	let size = fs.statSync(fil).size;
        if (size < 2000) {
          fs.writeFileSync('./small/'+fil, fs.readFileSync(fil,"utf-8"))
        } else {
          fs.createReadStream(fil).pipe(fs.createWriteStream('./big/' + fil))
        }
}

