function getRate(num, sum) {
    switch (true) {
        case num > sum:
        case num < 0:
        case Number.isInteger(sum) == false && !!sum:
            return "error"
            break;
        default:
            return creat(num, sum)
            break;
    }
}

function creat(num, sum = 5) {
    let arr = [];
    for (let i = 0; i < sum; i++) {
        if (i < Math.round(num)) {
            arr.push("★")
        } else {
            arr.push("☆")
        }
    }
    return arr
}
