let loadImg = (src)=>{
            return new Promise((resolve, reject)=>{
                // write your code here
                let img = new Image();
                img.src = src;
                document.body.appendChild(img);
                setTimeout(()=>{
                    resolve(true)
                    console.log(src)
                },2000)
            })
        }   
        const imgs = ['url1', 'url2', 'url3'];
        // 依次加载图片
        async function fAsync(imgs){
        // write your code here
            for(let i=0; i<imgs.length; i++){
                await loadImg(imgs[i])
            }
        }
        fAsync(imgs);

