async function CountById(id){
    let res = await fetchData('/api/students');
    console.log(res);
    let testArr = res.filter(item=>{
       return item.classroomId === id;
    });//获取同一教室中的所有学生

    let subject = testArr.map(async (item)=>{
        return await fetchData('/api/courses?filter='+item.id);
    });//此时subjec是个二维数组，包含同一教室中的所有学生，每一项是学生的科目

    let response = subject.map(async (item,i)=>{
        let eachLength = item.length;
        totscore=item.map(async (item2)=>{
             let score = await fetchData('/api/evaluation/'+item2.id+'?filter='+item[0].id);
                // 获取当前科目的分数
             return score.score;
        });
        let eachTotscore = totscore.reduce((tot,each)=>tot+each); // reduce函数获取总分

        return {
            id:item[0].id,
            name:item[0].name,
            average:Math.floor(eachTotscore/eachLength)
        }
    })
    return new Promise((resolve,reject)=>{
       if(response&&response.length){
           //若存在则成功
           resolve({response})
       }else{
           reject({response:'失败'})
       }
    })
}

