let count = 0;//
function qingwa(num, res) {
  if (num === 0) {//判断传进的参数是否为0 
    res = [];//返回一个空数组
  }
  var i = 1;//如果每次跳一个台阶
  while (i < 3) {//每次跳的台阶不大于3 ，每次跳1个或2个台阶
    if (num + i <= 10) {//如果传进的参数加上每次跳的台阶数 小于等于10
      let res1 = [...res];//
      res1.push(i);
      if (num + i === 10) {//如果参数加跳的台阶数等于10
        count++;//count++每次加1，
        console.log(count, res1);//一共有多少种跳法  每次怎么跳
      } else {
        qingwa(num + i, res1);
      }
    }
    i++
  }
}
