let oUl = document.getElementsByTagName('ul')[0];//查找ul元素
  let li = document.getElementsByTagName('li');//查找所有的li
  const {
      map,
      forEach
  } = [];
  li = map.call(li, item = & gt; {//遍历所有的li
                console.log//所有的li
                console.log(item)//每一项
      let key = 2 * item.getAttribute('key') - 1;//key的值就是每一项li得到一个为key的属性2*item-1   key=奇数
      item.setAttribute('key', key);//添加key属性属性值
      return item;//返回每一项
  });
  oUl.innerHTML = '';//定义ul内容为空
  forEach.call(li, item = & gt; {//运用map,call遍历每一项   
               console.log(item);//每一项
      oUl.appendChild(item);//最后在ul里添加到没一项属性key为奇数的值
  })

